const themeButton={'light':'<i class="fas fa-adjust" aria-hidden="true"></i>','dark':'<i class="fas fa-adjust fa-rotate-180" aria-hidden="true"></i>'};const currentTheme=()=>sessionStorage.getItem('theme');function setMode(theme){document.documentElement.setAttribute('data-theme',theme);sessionStorage.setItem('theme',theme);document.getElementById('theme-toggle').innerHTML=themeButton[theme]}function themeToggle(){let sessionPrefers=currentTheme();if(sessionPrefers==='light'){setMode('dark')}else{setMode('light')}}window.onload=function bootstrapTheme(){if(isAutoTheme){if(!currentTheme()){let browserPrefersDark=window.matchMedia('(prefers-color-scheme: dark)');browserPrefersDark.addEventListener('change',()=>{if(browserPrefersDark.matches){sessionStorage.setItem('theme','dark')}})}let sessionPrefers=currentTheme();setMode(sessionPrefers?sessionPrefers:'light')}};try{var elem=document.querySelector('.grid');var msnry=new Masonry(elem,{itemSelector:'.grid-item',columnWidth:'.grid-sizer',gutter:'.gutter-sizer',percentPosition:true});var imgLoad=imagesLoaded(elem);imgLoad.on('progress',function(instance,image){msnry.layout()})}catch(err){if(err instanceof ReferenceError){}else{throw err}}document.addEventListener("DOMContentLoaded",function(event){const pull=document.getElementById('pull');const menu=document.querySelector('nav ul');['click','touch'].forEach(function(e){pull.addEventListener(e,function(){menu.classList.toggle('hide')},false)});window.addEventListener('scroll',function(){const offset= -(window.scrollY||window.pageYOffset||document.body.scrollTop)/3;document.getElementById("main").style.backgroundPosition='100% '+(offset-50)+'px, 0%, center top'})});
